import actionTypes from "./types";

const initialState = {
  collections: [],
};

const shopReducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.UPDATE_COLLECTIONS:
      return {
        ...state,
        collections: action.payload,
      };
    default:
      return state;
  }
};

export default shopReducer;
